                                                <select value={qty} onChange={e => setQty(e.target.value)}>
                                                    
                                                </select>
                                            </div>
                                        </div>
                                    </li>
                                    <li>
                                        <button className="primary block">Ajouter au panier</button>
                                    </li>
                                    </>
                                )
                            }
                        </ul>
                    </div>
                </div>
            </div>
        </div>
        )}
      </div>
   
    )
}
